// Layout.js
import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../Common/Header/Header';
import Footer from '../Common/Footer/Footer';
import '../../../public/app-assets/css/vendors.css'// Import vendors.css
import '../../../public/app-assets/css/app.css'; // Import app.css
import '../../../public/app-assets/css/core/menu/menu-types/vertical-menu.css'; // Import vertical-menu.css
import '../../../public/app-assets/css/core/colors/palette-gradient.css'; // Import palette-gradient.css
import '../../../public/app-assets/vendors/css/cryptocoins/cryptocoins.css'; // Import cryptocoins.css
import '../../../public/assets/css/style.css'; // Import style.css

const Layout = ({ children }) => {
  return (
    <div className="container">
      <header>
        <h1><Header/></h1>
        <nav>
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to="/Employees">Employees</Link></li>
          </ul>
        </nav>
      </header>
      <main>{children}</main>
      <footer>
        <Footer/>
      </footer>
    </div>
  );
}

export default Layout;
